mapper.insert:
INSERT INTO employee(
    department_id
    , employee_number
    , sei
    , mei
    , sei_kana
    , mei_kana
    , gender
    , birth_date
    , hire_date

)
VALUES (
    #{departmentId}
    , #{employeeNumber}
    , #{sei}
    , #{mei}
    , #{seiKana}
    , #{meiKana}
    , #{gender}
    , #{birthDate}
    , #{hireDate}

)

mapper.update:
UPDATE
    employee
SET
    department_id = #{departmentId}
    , employee_number = #{employeeNumber}
    , sei = #{sei}
    , mei = #{mei}
    , sei_kana = #{seiKana}
    , mei_kana = #{meiKana}
    , gender = #{gender}
    , birth_date = #{birthDate}
    , hire_date = #{hireDate}

WHERE
    employee_id = #{employeeId}
    AND exclusive_flg = #{exclusiveFlg}

mapper.delete:
DELETE
    employee
WHERE
    employee_id = #{employeeId}
    AND exclusive_flg = #{exclusiveFlg}
