    employee_id
    , qualification_id

)
VALUES (
    #{employeeId}
    , #{qualificationId}

)

mapper.update:
UPDATE
    employee_qualification_map
SET
    employee_id = #{employeeId}
    , qualification_id = #{qualificationId}

WHERE
    employee_qualification_map_id = #{employeeQualificationMapId}
    AND exclusive_flg = #{exclusiveFlg}

mapper.delete:
